/*
** ===================================================================
**     Method      :  Display1_SetDir (bean BitsIO)
**
**     Description :
**         This method sets direction of the bean.
**     Parameters  :
**         NAME       - DESCRIPTION
**         Dir        - Direction to set (FALSE or TRUE)
**                      FALSE = Input, TRUE = Output
**     Returns     : Nothing
** ===================================================================
*/
void Display1_SetDir(bool Dir)
{
  if (Dir) {
    setReg8(PTAD, (getReg8(PTAD) & (~0xFF)) | Shadow_PTA); /* Put masked value on port */
    /* PTADD: PTADD7=1,PTADD6=1,PTADD5=1,PTADD4=1,PTADD3=1,PTADD2=1,PTADD1=1,PTADD0=1 */
    setReg8(PTADD, 0xFF);               
  } else { /* !Dir */
    /* PTADD: PTADD7=0,PTADD6=0,PTADD5=0,PTADD4=0,PTADD3=0,PTADD2=0,PTADD1=0,PTADD0=0 */
    setReg8(PTADD, 0x00);               
  } /* !Dir */
}

