void PWM_init( PWMTimer * pwm,byte pin){
  pwms[pin]=pwm;
  TIOS |= 1<<pin;
  TTOV &= ~(1<<pin);
}

#pragma INLINE
void dis_timer_interrupt(byte timer){
  byte pos = 1<<timer;
  TIE &= ~pos;                       /* Disable interrupt */    
}

#pragma INLINE
void en_timer_interrupt(byte timer){
  byte pos = 1<<timer;
  TIE |= pos;                       /* Disable interrupt */    
}

/* Setea la variable del duty de forma segura mientras se esta 
en modo proporcional*/
void pwm_timer_set_periodo( PWMTimer * pwm,ulong periodo){
  uchar salida = pwm->pinNum;
  dis_timer_interrupt(salida);
  pwm->ticksPeriodo = periodo;
  en_timer_interrupt(salida);
}
/* Setea la variable del duty de forma segura mientras se esta 
en modo proporcional*/
void pwm_timer_set_duty( PWMTimer * pwm,ulong duty){
  uchar salida = pwm->pinNum;
  dis_timer_interrupt(salida);
  pwm->ticksDuty =  duty;
  en_timer_interrupt(salida);
}
/*
** ===================================================================
**     Method      :  TO1_SetPeriod (bean TimerOut)
**
**     Description :
**         This method sets the new period of the output signal. The
**         period is expressed in microseconds as a 16-bit unsigned
**         integer number.
**         This method is available only if the runtime setting type
**         'from interval' is selected in the Timing dialog box in
**         the Runtime setting area.
**     Parameters  :
**         NAME            - DESCRIPTION
**         Time            - Period to set [in microseconds]
**                      (4 to 10000 microseconds)
**     Returns     :
**         ---             - Error code, possible codes:
**                           ERR_OK - OK
**                           ERR_SPEED - This device does not work in
**                           the active speed mode
**                           ERR_MATH - Overflow during evaluation
**                           ERR_RANGE - Parameter out of range
** ===================================================================
*/
byte setPWM_period( PWMTimer * pwm,byte Mode)
{
  if (Mode>PWM_MAX_VALUE_PERIODS)
    return ERR_RANGE;
  if(pwm_timer_isEnable(pwm->pinNum))
    pwm_timer_set_periodo(pwm,PeriodsTicks[Mode]);
  else
    pwm->ticksPeriodo = PeriodsTicks[Mode];  
  

  return ERR_OK;                       /* OK */ 
}

bool pwm_timer_isEnable(uchar salida){
  return  TIE & 1<<salida; 
}

byte PWM_SetRatio16( PWMTimer * pwm,word Ratio)		/*Ratio es un valor de 0 a 1000*/
{
  ulong duty;
  ulong periodo =pwm->ticksPeriodo;
  uchar salida = pwm->pinNum;
  
  if (Ratio >1000 || salida>PWM_NUM_SALIDAS)    /* Is the given value out of range? */
    return ERR_RANGE;                  /* If yes then error */
  
  if(pwm_timer_isEnable(salida)){
    if (Ratio==1000)
      duty = periodo;
    else if(Ratio==0)
      duty = 0;
    else if (periodo<4000000)		 /*Verifico los valores para decidir que operacion hacer debido a los limites de un long*/
      duty = (periodo*Ratio)/1000;
    else
      duty = (periodo/1000)*Ratio;
    
    if(duty<30){//15 es el minimo tiempo para asegurar la corrida de la interrupcion  
      if(duty<15) 
        duty=0;
      else 
        duty=30;  
    } 
    else{
      long dutyfree=periodo-duty;
      
      if (dutyfree<30){
        if(dutyfree<15) 
          duty=periodo;
        else 
          duty=periodo-30;      
      }
    }
    pwm_timer_set_duty(pwm,duty);
  
  }else{
     if(Ratio==0){
      pwm->ticksDuty =0;
  	  PWM_ClrValue(salida);
     }else{
      pwm->ticksDuty =1000;	
      Ratio=1000;
  	  PWM_SetValue(salida);  
     }
  }
  return ERR_OK;                       /* OK */
}

