void Test_SetDir(bool Dir)
{
  if (Dir) {
    setReg8(PTBD, (getReg8(PTBD) & (~0xF0)) | (Shadow_PTB & 0xF0)); /* Put masked value on port */
    /* PTBDD: PTBDD7=1,PTBDD6=1,PTBDD5=1,PTBDD4=1 */
    setReg8Bits(PTBDD, 0xF0);           
  } else { /* !Dir */
    /* PTBDD: PTBDD7=0,PTBDD6=0,PTBDD5=0,PTBDD4=0 */
    clrReg8Bits(PTBDD, 0xF0);           
  } /* !Dir */
}

